from flask import request,Flaskfrom flask_cors import CORSfrom point import locate_pointimport jsonapp = Flask(__name__)CORS(app)@app.route("/")def hello():    return "Hello World!"@app.route("/data_points", methods=['POST'])def parse_request():    print(request.data)    dataDict = json.loads(request.data)    print(dataDict)    url = dataDict['url'].encode("utf-8")    step = dataDict['step']    res = locate_point(url, step)    return resapp.run(host='localhost')